mapper.select:
SELECT


,jiaolian.jiaolian_name as jiaolianName
,jiaolian.jiaolian_phone as jiaolianPhone
,jiaolian.jiaolian_id_number as jiaolianIdNumber
,jiaolian.jiaolian_photo as jiaolianPhoto
,jiaolian.jiaolian_types as jiaolianTypes
,jiaolian.jiaolian_email as jiaolianEmail
,jiaolian.jiaolian_delete as jiaolianDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.yonghu_delete as yonghuDelete

FROM jiaolian_collection  a
left JOIN jiaolian jiaolian ON a.jiaolian_id = jiaolian.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.jiaolian_id = #{params.jiaolianId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.jiaolian_collection_types = #{params.jiaolianCollectionTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.jiaolian_id IS NOT NULL


        and jiaolian.jiaolian_name like CONCAT('%',#{params.jiaolianName},'%')


        and jiaolian.jiaolian_phone like CONCAT('%',#{params.jiaolianPhone},'%')


        and jiaolian.jiaolian_id_number like CONCAT('%',#{params.jiaolianIdNumber},'%')


        and jiaolian.jiaolian_types = #{params.jiaolianTypes}



        and jiaolian.jiaolian_email like CONCAT('%',#{params.jiaolianEmail},'%')


          and jiaolian.jiaolian_delete >= #{params.jiaolianDeleteStart}   


          and jiaolian.jiaolian_delete <= #{params.jiaolianDeleteEnd}   


        and jiaolian.jiaolian_delete = #{params.jiaolianDelete}


          and UNIX_TIMESTAMP(jiaolian.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jiaolian.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


          and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


        and yonghu.yonghu_delete = #{params.yonghuDelete}


          and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
